import React from "react";
import { NavLink } from "react-router-dom";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
);

const Dashboard = () => {

  const kpiData = [
    { title: "Total Sales", value: "‚Çπ42.75 Cr", icon: "üí∞" },
    { title: "Total Products Sold", value: "5.1 L", icon: "üì¶" },
    { title: "Avg Monthly Sales", value: "‚Çπ285.00 L", icon: "üìà" },
    { title: "Total Stores Analyzed", value: "75", icon: "üè¨" }
  ];

  const salesTrendData = {
    labels: ["2020", "2021", "2022", "2023", "2024"],
    datasets: [
      {
        label: "Total Sales",
        data: [4, 6, 8, 11, 14],
        borderColor: "#f97316",
        backgroundColor: "#f97316",
        tension: 0.4
      }
    ]
  };

  const regionData = {
    labels: ["North", "South", "East", "West"],
    datasets: [
      {
        data: [11.2, 10.5, 9.8, 9.3],
        backgroundColor: "#f97316"
      }
    ]
  };

  return (
    <div style={{ background: "#f8fafc", minHeight: "100vh", padding: "20px" }}>

      {/* ================= NAVBAR ================= */}
      <div style={navbar}>
        <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
          <div style={logo}>üõí</div>
          <div>
            <h3 style={{ margin: 0 }}>Big Bazaar Sales Prediction System</h3>
            <p style={subtitle}>Business Performance Overview & Analysis</p>
          </div>
        </div>

        <div style={{ display: "flex", gap: "10px" }}>
          <NavLink to="/" style={({ isActive }) => navBtn(isActive)}>Dashboard</NavLink>
          <NavLink to="/analysis" style={({ isActive }) => navBtn(isActive)}>Analysis</NavLink>
          <NavLink to="/predict" style={({ isActive }) => navBtn(isActive)}>Sales Prediction</NavLink>
        </div>
      </div>

      {/* ================= FILTERS ================= */}
<div style={card}>
  <h4 style={{ marginBottom: "12px" }}>Filters</h4>
  <div style={{ display: "flex", gap: "14px", flexWrap: "wrap" }}>
    <select style={filter}><option>All Years</option></select>
    <select style={filter}><option>All Categories</option></select>
    <select style={filter}><option>All Locations</option></select>
    <select style={filter}><option>All Outlet Types</option></select>
  </div>
</div>


      {/* ================= KPI CARDS ================= */}
      <div style={grid}>
        {kpiData.map((k, i) => (
          <div key={i} style={card}>
            <div style={{ fontSize: "22px" }}>{k.icon}</div>
            <p style={{ color: "#64748b" }}>{k.title}</p>
            <h2>{k.value}</h2>
          </div>
        ))}
      </div>

      {/* ================= CHARTS ================= */}
      <div style={{ display: "grid", gridTemplateColumns: "2fr 1fr", gap: "20px", marginTop: "20px" }}>
        <div style={card}>
          <h4>Year-wise Sales Trend</h4>
          <Line data={salesTrendData} />
        </div>

        <div style={card}>
          <h4>Regional Analysis</h4>
          <Bar data={regionData} options={{ indexAxis: "y" }} />
        </div>
      </div>

    </div>
  );
};

/* ================= STYLES ================= */

const navbar = {
  background: "#fff",
  padding: "14px 22px",
  borderRadius: "14px",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
  marginBottom: "24px"
};

const logo = {
  width: "38px",
  height: "38px",
  background: "#f97316",
  borderRadius: "10px",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  color: "#fff",
  fontSize: "18px"
};

const subtitle = {
  margin: 0,
  fontSize: "13px",
  color: "#64748b"
};

const navBtn = (active) => ({
  padding: "8px 16px",
  borderRadius: "999px",
  textDecoration: "none",
  background: active ? "#f97316" : "#f1f5f9",
  color: active ? "#fff" : "#334155",
  transition: "0.3s"
});

const grid = {
  display: "grid",
  gridTemplateColumns: "repeat(4,1fr)",
  gap: "16px"
};

const card = {
  background: "#fff",
  padding: "18px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};

const filter = {
  padding: "10px 14px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb",
  minWidth: "180px",
  cursor: "pointer"
};

export default Dashboard;
