import React, { useEffect, useState } from "react";
import axios from "axios";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
);

const Dashboard = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    fetchDashboard();
  }, []);

  const fetchDashboard = async () => {
    try {
      const res = await axios.get("http://localhost:5001/api/dashboard");
      setData(res.data);
    } catch (err) {
      console.error("Dashboard API error", err);
    }
  };

  if (!data) return <p>Loading dashboard...</p>;

  /* ===== CHART DATA ===== */

  const yearWiseChart = {
    labels: Object.keys(data.charts.yearWiseSales),
    datasets: [
      {
        label: "Total Sales",
        data: Object.values(data.charts.yearWiseSales),
        borderColor: "#f97316",
        backgroundColor: "#f97316",
        tension: 0.4
      }
    ]
  };

  const regionChart = {
    labels: Object.keys(data.charts.regionSales),
    datasets: [
      {
        data: Object.values(data.charts.regionSales),
        backgroundColor: "#f97316"
      }
    ]
  };

  return (
    <div style={{ padding: "20px", background: "#f8fafc", minHeight: "100vh" }}>
      <h2>Dashboard</h2>

      {/* KPIs */}
      <div style={{ display: "grid", gridTemplateColumns: "repeat(3,1fr)", gap: "16px" }}>
        <div style={card}>üí∞ Total Sales<br /><b>‚Çπ{(data.kpis.totalSales / 1e7).toFixed(2)} Cr</b></div>
        <div style={card}>üì¶ Units Sold<br /><b>{data.kpis.totalUnits}</b></div>
        <div style={card}>üè¨ Stores<br /><b>{data.kpis.totalStores}</b></div>
      </div>

      {/* Charts */}
      <div style={{ display: "grid", gridTemplateColumns: "2fr 1fr", gap: "20px", marginTop: "30px" }}>
        <div style={card}>
          <h4>Year-wise Sales</h4>
          <Line data={yearWiseChart} />
        </div>

        <div style={card}>
          <h4>Region-wise Sales</h4>
          <Bar data={regionChart} options={{ indexAxis: "y" }} />
        </div>
      </div>
    </div>
  );
};

const card = {
  background: "#fff",
  padding: "20px",
  borderRadius: "12px",
  boxShadow: "0 4px 12px rgba(0,0,0,0.05)"
};

export default Dashboard;
