import React, { useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";

const SalesPrediction = () => {
  const [form, setForm] = useState({
    category: "Grocery",
    outletType: "Supermarket",
    storeSize: "Medium",
    region: "North",
    year: "2025"
  });

  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const predictSales = async () => {
    setLoading(true);
    try {
      const res = await axios.post("http://localhost:5001/api/predict", form);
      setResult(res.data);
    } catch (err) {
      alert("Prediction failed");
    }
    setLoading(false);
  };

  const downloadReport = () => {
    if (!result) return;

    const csv = `Category,Outlet Type,Store Size,Region,Year,Predicted Sales\n
${form.category},${form.outletType},${form.storeSize},${form.region},${form.year},${result.predictedSales}`;

    const blob = new Blob([csv], { type: "text/csv" });
    const link = document.createElement("a");
    link.href = URL.createObjectURL(blob);
    link.download = "sales_prediction_report.csv";
    link.click();
  };

  return (
    <div style={page}>
      <Navbar />

      <h2>Sales Prediction</h2>
      <p style={subtitle}>
        Predict future sales based on selected business parameters
      </p>

      {/* ===== FORM ===== */}
      <div style={card}>
        <div style={grid}>
          <select name="category" onChange={handleChange} style={input}>
            <option>Grocery</option>
            <option>Electronics</option>
            <option>Clothing</option>
            <option>Home & Kitchen</option>
          </select>

          <select name="outletType" onChange={handleChange} style={input}>
            <option>Supermarket</option>
            <option>Hypermarket</option>
            <option>Convenience Store</option>
          </select>

          <select name="storeSize" onChange={handleChange} style={input}>
            <option>Small</option>
            <option>Medium</option>
            <option>Large</option>
          </select>

          <select name="region" onChange={handleChange} style={input}>
            <option>North</option>
            <option>South</option>
            <option>East</option>
            <option>West</option>
          </select>

          <select name="year" onChange={handleChange} style={input}>
            <option>2025</option>
            <option>2026</option>
          </select>
        </div>

        <button onClick={predictSales} style={btn}>
          {loading ? "Predicting..." : "Predict Sales"}
        </button>
      </div>

      {/* ===== RESULT ===== */}
      {result && (
        <div style={{ ...card, marginTop: 24 }}>
          <h3>Prediction Result</h3>
          <p>
            <strong>Expected Sales:</strong>{" "}
            <span style={{ color: "#f97316", fontSize: 20 }}>
              â‚¹{result.predictedSales} Cr
            </span>
          </p>

          <button onClick={downloadReport} style={downloadBtn}>
            Download Report
          </button>
        </div>
      )}
    </div>
  );
};

/* ===== STYLES ===== */
const page = { background: "#f8fafc", minHeight: "100vh", padding: 20 };
const subtitle = { color: "#64748b", marginBottom: 20 };
const card = {
  background: "#fff",
  padding: 18,
  borderRadius: 14,
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};
const grid = {
  display: "grid",
  gridTemplateColumns: "repeat(3,1fr)",
  gap: 12
};
const input = {
  padding: "10px 12px",
  borderRadius: 10,
  border: "1px solid #e5e7eb"
};
const btn = {
  marginTop: 16,
  padding: "10px",
  background: "#f97316",
  color: "#fff",
  border: "none",
  borderRadius: 10,
  cursor: "pointer"
};
const downloadBtn = {
  marginTop: 12,
  padding: "8px 14px",
  background: "#22c55e",
  color: "#fff",
  border: "none",
  borderRadius: 10,
  cursor: "pointer"
};

export default SalesPrediction;
