import React from "react";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  ArcElement,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  ArcElement,
  BarElement,
  Tooltip,
  Legend
);

const Analysis = () => {

  /* ===== CATEGORY SALES ===== */
  const categorySales = {
    labels: ["Grocery", "Electronics", "Clothing", "Home & Kitchen"],
    datasets: [
      {
        data: [32, 28, 22, 18],
        backgroundColor: ["#f97316", "#fb923c", "#fdba74", "#fed7aa"]
      }
    ]
  };

  /* ===== STORE TYPE SALES ===== */
  const storeTypeSales = {
    labels: ["Supermarket Type 1", "Supermarket Type 2", "Grocery Store"],
    datasets: [
      {
        label: "Sales (â‚¹ Cr)",
        data: [18, 15, 9],
        backgroundColor: "#f97316"
      }
    ]
  };

  /* ===== LOCATION PERFORMANCE ===== */
  const locationSales = {
    labels: ["Tier 1 Cities", "Tier 2 Cities", "Tier 3 Cities"],
    datasets: [
      {
        label: "Sales Contribution",
        data: [40, 35, 25],
        backgroundColor: "#fb923c"
      }
    ]
  };

  return (
    <div style={{ background: "#f8fafc", minHeight: "100vh", padding: "20px" }}>

      {/* ================= NAVBAR ================= */}
      <div style={navbar}>
        <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
          <div style={logo}>ðŸ“Š</div>
          <div>
            <h3 style={{ margin: 0 }}>Big Bazaar Sales Analysis</h3>
            <p style={subtitle}>Detailed business insights & trends</p>
          </div>
        </div>

        <div style={{ display: "flex", gap: "10px" }}>
          <button style={navBtn(false)}>Dashboard</button>
          <button style={navBtn(true)}>Analysis</button>
          <button style={navBtn(false)}>Sales Prediction</button>
        </div>
      </div>

      {/* ================= FILTERS ================= */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={{ display: "flex", gap: "14px", flexWrap: "wrap" }}>
          <select style={filter}><option>All Years</option></select>
          <select style={filter}><option>All Categories</option></select>
          <select style={filter}><option>All Locations</option></select>
          <select style={filter}><option>All Store Types</option></select>
        </div>
      </div>

      {/* ================= CHART ROW 1 ================= */}
      <div style={grid2}>
        <div style={card}>
          <h4>Category-wise Sales Contribution</h4>
          <Pie data={categorySales} />
        </div>

        <div style={card}>
          <h4>Store Type Performance</h4>
          <Bar data={storeTypeSales} />
        </div>
      </div>

      {/* ================= CHART ROW 2 ================= */}
      <div style={{ ...card, marginTop: "20px" }}>
        <h4>City Tier-wise Sales Performance</h4>
        <Bar data={locationSales} />
      </div>

      {/* ================= INSIGHTS ================= */}
      <div style={{ ...card, marginTop: "20px" }}>
        <h4>Key Business Insights</h4>
        <ul style={{ color: "#334155", lineHeight: "1.8" }}>
          <li>Grocery category contributes the highest overall sales.</li>
          <li>Supermarket Type 1 stores show stronger performance.</li>
          <li>Tier 2 cities show faster growth compared to Tier 1.</li>
          <li>Electronics category has high revenue with lower unit sales.</li>
        </ul>
      </div>

    </div>
  );
};

/* ================= STYLES ================= */

const navbar = {
  background: "#fff",
  padding: "14px 22px",
  borderRadius: "14px",
  display: "flex",
  justifyContent: "space-between",
  alignItems: "center",
  boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
  marginBottom: "24px"
};

const logo = {
  width: "38px",
  height: "38px",
  background: "#f97316",
  borderRadius: "10px",
  display: "flex",
  alignItems: "center",
  justifyContent: "center",
  color: "#fff",
  fontSize: "18px"
};

const subtitle = {
  margin: 0,
  fontSize: "13px",
  color: "#64748b"
};

const navBtn = (active) => ({
  padding: "8px 16px",
  borderRadius: "999px",
  border: "none",
  cursor: "pointer",
  background: active ? "#f97316" : "#f1f5f9",
  color: active ? "#fff" : "#334155",
  transition: "all 0.25s ease"
});

const card = {
  background: "#fff",
  padding: "20px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)",
  marginBottom: "20px"
};

const filter = {
  padding: "10px 14px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb",
  minWidth: "180px"
};

const grid2 = {
  display: "grid",
  gridTemplateColumns: "1fr 1fr",
  gap: "20px",
  marginTop: "20px"
};

export default Analysis;
