import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

const Analysis = () => {
  const [data, setData] = useState(null);
  const [filters, setFilters] = useState({
    year: "All",
    category: "All",
    region: "All",
    outletType: "All"
  });

  /* ================= FETCH DATA ================= */
  useEffect(() => {
    axios
      .get("http://localhost:5001/api/analysis", { params: filters })
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, [filters]);

  /* ================= SAFE GUARD ================= */
  if (!data || !data.categoryMetrics || data.categoryMetrics.length === 0) {
    return (
      <div style={page}>
        <Navbar />
        <p style={{ padding: 20 }}>No data available for selected filters</p>
      </div>
    );
  }

  /* ================= CATEGORY BAR ================= */
  const categoryChart = {
    labels: data.categoryMetrics.map(c => c.category),
    datasets: [
      {
        label: "Sales (Cr)",
        data: data.categoryMetrics.map(c => c.totalSales / 1e7),
        backgroundColor: "#f97316"
      },
      {
        label: "Profit (Cr)",
        data: data.categoryMetrics.map(c => (c.totalSales * c.profitMargin) / 100 / 1e7),
        backgroundColor: "#22c55e"
      }
    ]
  };

  /* ================= PIE DATA ================= */
  const outletPie = {
    labels: Object.keys(data.outletTypeSales),
    datasets: [
      {
        data: Object.values(data.outletTypeSales).map(v => v / 1e7),
        backgroundColor: ["#f97316", "#fb923c", "#fdba74"]
      }
    ]
  };

  const agePie = {
    labels: Object.keys(data.ageGroupSales),
    datasets: [
      {
        data: Object.values(data.ageGroupSales).map(v => v / 1e7),
        backgroundColor: ["#f97316", "#fb923c", "#fdba74", "#fed7aa"]
      }
    ]
  };

  const cityTierPie = {
    labels: Object.keys(data.cityTierSales),
    datasets: [
      {
        data: Object.values(data.cityTierSales).map(v => v / 1e7),
        backgroundColor: ["#f97316", "#fb923c", "#fdba74"]
      }
    ]
  };

  /* ================= RENDER ================= */
  return (
    <div style={page}>
      <Navbar />

      {/* ===== HEADER ===== */}
      <h2>Deep Sales Analysis</h2>
      <p style={subtitle}>
        Understand patterns, trends, and insights behind the numbers
      </p>

      {/* ===== FILTERS ===== */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={filterRow}>
          <select style={filter} onChange={e => setFilters({ ...filters, year: e.target.value })}>
            <option value="All">All Years</option>
            <option value="2020">2020</option>
            <option value="2021">2021</option>
            <option value="2022">2022</option>
            <option value="2023">2023</option>
            <option value="2024">2024</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, category: e.target.value })}>
            <option value="All">All Categories</option>
            <option value="Electronics">Electronics</option>
            <option value="Grocery">Grocery</option>
            <option value="Clothing">Clothing</option>
            <option value="Home & Kitchen">Home & Kitchen</option>
            <option value="Personal Care">Personal Care</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, region: e.target.value })}>
            <option value="All">All Regions</option>
            <option value="North">North</option>
            <option value="South">South</option>
            <option value="East">East</option>
            <option value="West">West</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, outletType: e.target.value })}>
            <option value="All">All Outlet Types</option>
            <option value="Supermarket">Supermarket</option>
            <option value="Hypermarket">Hypermarket</option>
            <option value="Convenience Store">Convenience Store</option>
          </select>
        </div>
      </div>

      {/* ===== CATEGORY ANALYSIS ===== */}
      <div style={{ ...card, marginTop: 24 }}>
        <h4>Product Category Analysis</h4>
        <Bar data={categoryChart} height={180} options={{ maintainAspectRatio: false }} />
      </div>

      {/* ===== TABLE ===== */}
      <div style={{ ...card, marginTop: 24 }}>
        <h4>Detailed Product Metrics</h4>
        <table style={table}>
          <thead>
            <tr>
              <th>Category</th>
              <th>Total Sales</th>
              <th>Units Sold</th>
              <th>Avg Price</th>
              <th>Profit Margin</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {data.categoryMetrics.map((c, i) => (
              <tr key={i}>
                <td>{c.category}</td>
                <td>₹{(c.totalSales / 1e7).toFixed(2)} Cr</td>
                <td>{c.unitsSold}</td>
                <td>₹{Math.round(c.avgPrice)}</td>
                <td>{c.profitMargin.toFixed(1)}%</td>
                <td><span style={badge}>{c.status}</span></td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* ===== STORE & CUSTOMER ===== */}
      <div style={{ display: "grid", gridTemplateColumns: "1fr 1fr", gap: 24, marginTop: 24 }}>
        <div style={card}>
          <h4>Sales by Outlet Type</h4>
          <Pie data={outletPie} height={180} />
        </div>

        <div style={card}>
          <h4>Sales by Age Group</h4>
          <Pie data={agePie} height={180} />
        </div>
      </div>

      {/* ===== CITY TIER ===== */}
      <div style={{ ...card, marginTop: 24 }}>
        <h4>Sales by City Tier</h4>
        <Pie data={cityTierPie} height={180} />
      </div>
    </div>
  );
};

/* ================= STYLES ================= */
const page = { background: "#f8fafc", minHeight: "100vh", padding: 20 };
const subtitle = { color: "#64748b", marginBottom: 20 };

const card = {
  background: "#fff",
  padding: 18,
  borderRadius: 14,
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};

const filterRow = { display: "flex", gap: 14, flexWrap: "wrap" };

const filter = {
  padding: "10px 14px",
  borderRadius: 10,
  border: "1px solid #e5e7eb",
  minWidth: 160
};

const table = {
  width: "100%",
  borderCollapse: "collapse",
  marginTop: 10
};

const badge = {
  background: "#fee2e2",
  padding: "4px 10px",
  borderRadius: 999,
  fontSize: 12
};

export default Analysis;
