import React, { useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import { Line } from "react-chartjs-2";

const SalesPrediction = () => {
  const [form, setForm] = useState({
    itemType: "Dairy",
    price: 150,
    outletSize: "High",
    locationType: "Tier 2",
    year: 2025
  });

  const [result, setResult] = useState(null);

  const predictSales = async () => {
    const res = await axios.post("http://localhost:5001/api/predict", form);
    setResult(res.data);
  };

  const downloadReport = () => {
    window.open("http://localhost:5001/api/predict/report", "_blank");
  };

  return (
    <div style={{ padding: "20px", background: "#f8fafc", minHeight: "100vh" }}>
      <Navbar />

      <h2>Sales Prediction & Reports</h2>
      <p style={{ color: "#64748b" }}>
        Enter product and outlet details to predict future sales
      </p>

      <div style={{ display: "grid", gridTemplateColumns: "1fr 2fr", gap: "20px" }}>
        
        {/* INPUT FORM */}
        <div style={card}>
          <h4>ðŸ“‹ Input Details</h4>

          <label>Item Type</label>
          <select style={input} onChange={e => setForm({ ...form, itemType: e.target.value })}>
            <option>Dairy</option>
            <option>Grocery</option>
            <option>Clothing</option>
            <option>Electronics</option>
          </select>

          <label>Item Price (â‚¹)</label>
          <input
            type="number"
            style={input}
            value={form.price}
            onChange={e => setForm({ ...form, price: e.target.value })}
          />

          <label>Outlet Size</label>
          <select style={input} onChange={e => setForm({ ...form, outletSize: e.target.value })}>
            <option>High</option>
            <option>Medium</option>
            <option>Small</option>
          </select>

          <label>Outlet Location</label>
          <select style={input} onChange={e => setForm({ ...form, locationType: e.target.value })}>
            <option>Tier 1</option>
            <option>Tier 2</option>
            <option>Tier 3</option>
          </select>

          <label>Year</label>
          <input
            type="number"
            style={input}
            value={form.year}
            onChange={e => setForm({ ...form, year: e.target.value })}
          />

          <button style={btn} onClick={predictSales}>ðŸ“ˆ Predict Sales</button>
        </div>

        {/* RESULT */}
        <div>
          {result && (
            <>
              <div style={predictionCard}>
                <h4>ðŸ“Š Predicted Sales</h4>
                <h1>â‚¹{result.predictedSales.toLocaleString()}</h1>
                <p>Expected sales for selected configuration</p>

                <button style={downloadBtn} onClick={downloadReport}>
                  â¬‡ Download Report
                </button>
              </div>

              <div style={card}>
                <h4>ðŸ“‰ Sales Trend Comparison</h4>
                <Line
                  data={{
                    labels: result.trend.months,
                    datasets: [
                      {
                        label: "Historical Sales",
                        data: result.trend.historical,
                        borderColor: "#94a3b8"
                      },
                      {
                        label: "Predicted Sales",
                        data: result.trend.predicted,
                        borderColor: "#f97316"
                      }
                    ]
                  }}
                />
              </div>

              <div style={card}>
                <h4>ðŸ’¡ Business Insights</h4>
                <ul>
                  <li>Demand Level: <b>{result.insights.demand}</b></li>
                  <li>Category Performance: {result.insights.category}</li>
                  <li>Recommendation: {result.insights.recommendation}</li>
                </ul>
              </div>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

/* ================= STYLES ================= */

const card = {
  background: "#fff",
  padding: "18px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)",
  marginBottom: "18px"
};

const input = {
  width: "100%",
  padding: "10px",
  marginBottom: "12px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb"
};

const btn = {
  width: "100%",
  padding: "12px",
  background: "#f97316",
  color: "#fff",
  border: "none",
  borderRadius: "10px",
  cursor: "pointer"
};

const predictionCard = {
  background: "linear-gradient(135deg,#2563eb,#1d4ed8)",
  color: "#fff",
  padding: "20px",
  borderRadius: "14px",
  marginBottom: "18px"
};

const downloadBtn = {
  background: "#fff",
  color: "#1d4ed8",
  padding: "8px 14px",
  borderRadius: "8px",
  border: "none",
  cursor: "pointer"
};

export default SalesPrediction;
