import React, { useEffect, useState } from "react";
import axios from "axios";
import Navbar from "./Navbar";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  ArcElement,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  ArcElement,
  BarElement,
  Tooltip,
  Legend
);

const Analysis = () => {
  const [data, setData] = useState(null);
  const [filters, setFilters] = useState({
    year: "",
    category: "",
    region: "",
    outletType: ""
  });

  useEffect(() => {
    fetchAnalysis();
  }, [filters]);

  const fetchAnalysis = async () => {
    const res = await axios.get("http://localhost:5001/api/analysis", {
      params: filters
    });
    setData(res.data);
  };

  if (!data) return <p>Loading analysis...</p>;

  /* ===== CATEGORY SALES BAR ===== */
  const categoryBar = {
    labels: Object.keys(data.categoryMap),
    datasets: [
      {
        label: "Sales",
        data: Object.values(data.categoryMap).map(d => d.sales),
        backgroundColor: "#f97316"
      }
    ]
  };

  /* ===== OUTLET TYPE PIE (SMALL) ===== */
  const outletPie = {
    labels: Object.keys(data.outletSales),
    datasets: [
      {
        data: Object.values(data.outletSales),
        backgroundColor: ["#fb923c", "#f97316", "#fdba74"]
      }
    ]
  };

  /* ===== STORE SIZE BAR ===== */
  const sizeBar = {
    labels: Object.keys(data.sizeSales),
    datasets: [
      {
        label: "Avg Sales",
        data: Object.values(data.sizeSales),
        backgroundColor: "#0ea5e9"
      }
    ]
  };

  /* ===== AGE GROUP PIE ===== */
  const agePie = {
    labels: Object.keys(data.ageSales),
    datasets: [
      {
        data: Object.values(data.ageSales),
        backgroundColor: ["#22c55e", "#16a34a", "#4ade80", "#86efac"]
      }
    ]
  };

  /* ===== CITY TIER PIE ===== */
  const cityTierPie = {
    labels: Object.keys(data.cityTierSales),
    datasets: [
      {
        data: Object.values(data.cityTierSales),
        backgroundColor: ["#6366f1", "#818cf8", "#a5b4fc"]
      }
    ]
  };

  return (
    <div style={{ background: "#f8fafc", minHeight: "100vh", padding: "20px" }}>
      <Navbar />

      {/* ================= FILTERS ================= */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={{ display: "flex", gap: "12px", flexWrap: "wrap" }}>
          <select style={filter} onChange={e => setFilters({ ...filters, year: e.target.value })}>
            <option value="">All Years</option>
            <option>2020</option>
            <option>2021</option>
            <option>2022</option>
            <option>2023</option>
            <option>2024</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, category: e.target.value })}>
            <option value="">All Categories</option>
            {Object.keys(data.categoryMap).map(c => (
              <option key={c}>{c}</option>
            ))}
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, region: e.target.value })}>
            <option value="">All Regions</option>
            <option>North</option>
            <option>South</option>
            <option>East</option>
            <option>West</option>
          </select>
        </div>
      </div>

      {/* ================= ROW 1 ================= */}
      <div style={grid2}>
        <div style={card}>
          <h4>Category-wise Sales</h4>
          <Bar data={categoryBar} />
        </div>

        <div style={{ ...card, maxWidth: "420px" }}>
          <h4>Sales by Outlet Type</h4>
          <Pie data={outletPie} />
        </div>
      </div>

      {/* ================= ROW 2 ================= */}
      <div style={grid2}>
        <div style={card}>
          <h4>Sales by Store Size</h4>
          <Bar data={sizeBar} />
        </div>

        <div style={{ ...card, maxWidth: "420px" }}>
          <h4>Sales by Age Group</h4>
          <Pie data={agePie} />
        </div>
      </div>

      {/* ================= ROW 3 ================= */}
      <div style={{ ...card, maxWidth: "420px" }}>
        <h4>Sales by City Tier</h4>
        <Pie data={cityTierPie} />
      </div>
    </div>
  );
};

/* ================= STYLES ================= */

const card = {
  background: "#fff",
  padding: "18px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)",
  marginBottom: "20px"
};

const grid2 = {
  display: "grid",
  gridTemplateColumns: "2fr 1fr",
  gap: "20px"
};

const filter = {
  padding: "10px 14px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb",
  minWidth: "180px"
};

export default Analysis;
