import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import { Bar, Line, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend
);

const Analysis = () => {
  /* ================= FILTER STATE ================= */
  const [filters, setFilters] = useState({
    year: "",
    category: "",
    region: "",
    outletType: ""
  });

  const [data, setData] = useState(null);

  useEffect(() => {
    fetchAnalysis();
  }, [filters]);

  const fetchAnalysis = async () => {
    const res = await axios.get("http://localhost:5001/api/analysis", {
      params: filters
    });
    setData(res.data);
  };

if (!data || !data.category) return <p>Loading analysis...</p>;

  /* ================= CHART DATA ================= */

  const categorySalesProfit = {
    labels: data.category.labels,
    datasets: [
      {
        label: "Sales (₹ Cr)",
        data: data.category.sales,
        backgroundColor: "#f97316"
      },
      {
        label: "Profit (₹ Cr)",
        data: data.category.profit,
        backgroundColor: "#22c55e"
      }
    ]
  };

  const ageDistribution = {
    labels: data.age.labels,
    datasets: [
      {
        data: data.age.sales,
        backgroundColor: ["#f97316", "#fb923c", "#fdba74", "#fed7aa"]
      }
    ]
  };

  const cityTierDistribution = {
    labels: data.cityTier.labels,
    datasets: [
      {
        data: data.cityTier.sales,
        backgroundColor: ["#f97316", "#fb923c", "#fdba74"]
      }
    ]
  };

  const stateSales = {
    labels: data.states.labels,
    datasets: [
      {
        label: "Sales (₹ Cr)",
        data: data.states.sales,
        backgroundColor: "#f97316"
      },
      {
        label: "Avg Store Sales (₹ L)",
        data: data.states.avg,
        borderColor: "#22c55e",
        tension: 0.4,
        type: "line"
      }
    ]
  };

  const regionGrowth = {
    labels: data.regions.labels,
    datasets: [
      {
        label: "Sales (₹ Cr)",
        data: data.regions.sales,
        backgroundColor: "#f97316"
      },
      {
        label: "Growth %",
        data: data.regions.growth,
        borderColor: "#22c55e",
        tension: 0.4,
        type: "line"
      }
    ]
  };

  return (
    <div style={{ padding: "20px", background: "#f8fafc", minHeight: "100vh" }}>
      <Navbar />

      {/* ================= FILTERS ================= */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={filterRow}>
          <select style={filter} onChange={e => setFilters({ ...filters, year: e.target.value })}>
            <option value="">All Years</option>
            <option>2020</option>
            <option>2021</option>
            <option>2022</option>
            <option>2023</option>
            <option>2024</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, category: e.target.value })}>
            <option value="">All Categories</option>
            <option>Electronics</option>
            <option>Grocery</option>
            <option>Clothing</option>
            <option>Home & Kitchen</option>
            <option>Personal Care</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, region: e.target.value })}>
            <option value="">All Regions</option>
            <option>North</option>
            <option>South</option>
            <option>East</option>
            <option>West</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, outletType: e.target.value })}>
            <option value="">All Outlet Types</option>
            <option>Supermarket</option>
            <option>Convenience Store</option>
          </select>
        </div>
      </div>

      {/* ================= CATEGORY ANALYSIS ================= */}
      <div style={section}>
        <h3>Product Category Analysis</h3>
        <div style={grid2}>
          <div style={card}>
            <Bar data={categorySalesProfit} />
          </div>

          <div style={card}>
            <h4>High Performer</h4>
            <p style={{ color: "#16a34a", fontWeight: "600" }}>
              {data.highLow.high.name} – ₹{data.highLow.high.sales} Cr
            </p>

            <h4>Low Performer</h4>
            <p style={{ color: "#dc2626", fontWeight: "600" }}>
              {data.highLow.low.name} – ₹{data.highLow.low.sales} Cr
            </p>
          </div>
        </div>
      </div>

      {/* ================= CUSTOMER DEMOGRAPHICS ================= */}
      <div style={section}>
        <h3>Customer Demographics Analysis</h3>
        <div style={grid2}>
          <div style={card}><Pie data={ageDistribution} /></div>
          <div style={card}><Pie data={cityTierDistribution} /></div>
        </div>
      </div>

      {/* ================= GEOGRAPHIC ANALYSIS ================= */}
      <div style={section}>
        <h3>Geographic Location Analysis</h3>
        <div style={grid2}>
          <div style={card}><Bar data={stateSales} /></div>
          <div style={card}><Bar data={regionGrowth} /></div>
        </div>
      </div>
    </div>
  );
};

/* ================= STYLES ================= */

const card = {
  background: "#fff",
  padding: "18px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};

const section = { marginTop: "26px" };

const grid2 = {
  display: "grid",
  gridTemplateColumns: "2fr 1fr",
  gap: "18px"
};

const filterRow = {
  display: "flex",
  gap: "14px",
  flexWrap: "wrap"
};

const filter = {
  padding: "10px 14px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb",
  minWidth: "180px"
};

export default Analysis;
