import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

const Analysis = () => {
  const [data, setData] = useState(null);

  useEffect(() => {
    axios
      .get("http://localhost:5001/api/analysis")
      .then(res => setData(res.data))
      .catch(err => console.error(err));
  }, []);

  if (!data) return <p>Loading analysis...</p>;

  /* ================= CATEGORY CHART ================= */
  const categoryLabels = data.categoryMetrics.map(c => c.category);

  const categoryChart = {
    labels: categoryLabels,
    datasets: [
      {
        label: "Total Sales (Cr)",
        data: data.categoryMetrics.map(c => c.totalSales / 1e7),
        backgroundColor: "#f97316"
      }
    ]
  };

  const highPerformer = data.categoryMetrics.reduce((a, b) =>
    a.totalSales > b.totalSales ? a : b
  );

  const lowPerformer = data.categoryMetrics.reduce((a, b) =>
    a.totalSales < b.totalSales ? a : b
  );

  return (
    <div style={page}>
      <Navbar />

      {/* ===== HEADER ===== */}
      <h2>Deep Sales Analysis</h2>
      <p style={subtitle}>
        Understand patterns, trends, and insights behind the numbers
      </p>

      {/* ================= PRODUCT CATEGORY ================= */}
      <div style={card}>
        <h4>Product Category Analysis</h4>

        <div style={grid2}>
          <div>
            <Bar data={categoryChart} height={120} />
          </div>

          <div>
            <div style={goodBox}>
              <strong>High Performer</strong>
              <p>{highPerformer.category}</p>
              <span>₹{(highPerformer.totalSales / 1e7).toFixed(2)} Cr</span>
            </div>

            <div style={badBox}>
              <strong>Low Performer</strong>
              <p>{lowPerformer.category}</p>
              <span>₹{(lowPerformer.totalSales / 1e7).toFixed(2)} Cr</span>
            </div>
          </div>
        </div>
      </div>

      {/* ================= TABLE ================= */}
      <div style={{ ...card, marginTop: 20 }}>
        <h4>Detailed Product Metrics</h4>
        <table style={table}>
          <thead>
            <tr>
              <th>Category</th>
              <th>Total Sales</th>
              <th>Units Sold</th>
              <th>Avg Price</th>
              <th>Profit %</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {data.categoryMetrics.map((c, i) => (
              <tr key={i}>
                <td>{c.category}</td>
                <td>₹{(c.totalSales / 1e7).toFixed(2)} Cr</td>
                <td>{c.unitsSold}</td>
                <td>₹{Math.round(c.avgPrice)}</td>
                <td>{c.profitMargin.toFixed(1)}%</td>
                <td>
                  <span style={badge}>{c.status}</span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {/* ================= STORE & OUTLET ================= */}
      <div style={{ ...card, marginTop: 24 }}>
        <h4>Store & Outlet Analysis</h4>

        <div style={grid2}>
          <Bar
            data={{
              labels: Object.keys(data.outletTypeSales),
              datasets: [
                {
                  data: Object.values(data.outletTypeSales),
                  backgroundColor: "#fb923c"
                }
              ]
            }}
            height={120}
          />

          <Bar
            data={{
              labels: Object.keys(data.storeSizeSales),
              datasets: [
                {
                  data: Object.values(data.storeSizeSales),
                  backgroundColor: "#f97316"
                }
              ]
            }}
            height={120}
          />
        </div>
      </div>

      {/* ================= CUSTOMER ================= */}
      <div style={{ ...card, marginTop: 24 }}>
        <h4>Customer Demographics</h4>

        <div style={grid2}>
          <Bar
            data={{
              labels: Object.keys(data.ageGroupSales),
              datasets: [
                {
                  data: Object.values(data.ageGroupSales),
                  backgroundColor: "#22c55e"
                }
              ]
            }}
            height={120}
          />

          <Bar
            data={{
              labels: Object.keys(data.cityTierSales),
              datasets: [
                {
                  data: Object.values(data.cityTierSales),
                  backgroundColor: "#0ea5e9"
                }
              ]
            }}
            height={120}
          />
        </div>
      </div>

      {/* ================= GEO ================= */}
      <div style={{ ...card, marginTop: 24 }}>
        <h4>Geographic Analysis</h4>

        <div style={grid2}>
          <Bar
            data={{
              labels: Object.keys(data.geographic.stateSales).slice(0, 6),
              datasets: [
                {
                  data: Object.values(data.geographic.stateSales).slice(0, 6),
                  backgroundColor: "#f97316"
                }
              ]
            }}
            height={120}
          />

          <Bar
            data={{
              labels: Object.keys(data.geographic.citySales).slice(0, 6),
              datasets: [
                {
                  data: Object.values(data.geographic.citySales).slice(0, 6),
                  backgroundColor: "#fb923c"
                }
              ]
            }}
            height={120}
          />
        </div>
      </div>
    </div>
  );
};

/* ================= STYLES ================= */
const page = { background: "#f8fafc", minHeight: "100vh", padding: 20 };
const subtitle = { color: "#64748b", marginBottom: 20 };
const card = {
  background: "#fff",
  padding: 18,
  borderRadius: 14,
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};
const grid2 = {
  display: "grid",
  gridTemplateColumns: "1fr 1fr",
  gap: 20
};
const goodBox = {
  background: "#ecfdf5",
  padding: 12,
  borderRadius: 12,
  marginBottom: 12
};
const badBox = {
  background: "#fef2f2",
  padding: 12,
  borderRadius: 12
};
const table = {
  width: "100%",
  borderCollapse: "collapse",
  marginTop: 10
};
const badge = {
  background: "#fee2e2",
  padding: "4px 10px",
  borderRadius: 999,
  fontSize: 12
};

export default Analysis;
