import React, { useState } from "react";
import Navbar from "./Navbar";
import {
  Bar,
  Pie,
  Line,
  Doughnut,
  Radar
} from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  PointElement,
  LineElement,
  RadialLinearScale,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  PointElement,
  LineElement,
  RadialLinearScale,
  Tooltip,
  Legend
);

const Analysis = () => {

  /* ================= FILTER STATE ================= */
  const [year, setYear] = useState("All");
  const [category, setCategory] = useState("All");
  const [region, setRegion] = useState("All");
  const [outlet, setOutlet] = useState("All");

  /* ================= BASE DATA ================= */
  const baseCategorySales = {
    Electronics: 9.5,
    Grocery: 5.0,
    "Home & Kitchen": 4.2,
    Clothing: 4.1,
    "Personal Care": 2.7
  };

  /* ================= FILTER LOGIC (DEMO) ================= */
  const filteredCategories =
    category === "All" ? Object.keys(baseCategorySales) : [category];

  const filteredSales =
    filteredCategories.map(cat => baseCategorySales[cat]);

  /* ================= CHART DATA ================= */

  const categoryRadar = {
    labels: filteredCategories,
    datasets: [{
      label: "Sales Strength (₹ Cr)",
      data: filteredSales,
      backgroundColor: "rgba(249,115,22,0.2)",
      borderColor: "#f97316"
    }]
  };

  const outletDoughnut = {
    labels: ["Hypermarket", "Supermarket", "Convenience"],
    datasets: [{
      data: [14, 8.5, 2.9],
      backgroundColor: ["#f97316", "#fb923c", "#fdba74"]
    }]
  };

  const stateTrend = {
    labels: ["Jan", "Feb", "Mar", "Apr", "May"],
    datasets: [{
      label: "Sales Trend (₹ Cr)",
      data: [3.2, 3.8, 4.0, 4.4, 4.9],
      borderColor: "#22c55e",
      tension: 0.4
    }]
  };

  const ageBar = {
    labels: ["18-25", "26-35", "36-45", "46+"],
    datasets: [{
      label: "Avg Spend",
      data: [3.5, 5.0, 4.0, 4.2],
      backgroundColor: "#f97316"
    }]
  };

  /* ================= OPTIONS (SMALL SIZE) ================= */
  const chartBox = { height: "260px" };
  const chartOptions = { maintainAspectRatio: false };

  return (
    <div style={page}>
      <Navbar />

      <h2>Deep Sales Analysis</h2>
      <p style={subtitle}>Smart insights with interactive filters</p>

      {/* FILTERS */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={filters}>
          <select onChange={e => setYear(e.target.value)}>
            <option>All</option>
            <option>2023</option>
            <option>2024</option>
          </select>

          <select onChange={e => setCategory(e.target.value)}>
            <option>All</option>
            <option>Electronics</option>
            <option>Grocery</option>
            <option>Clothing</option>
          </select>

          <select onChange={e => setRegion(e.target.value)}>
            <option>All</option>
            <option>North</option>
            <option>South</option>
          </select>

          <select onChange={e => setOutlet(e.target.value)}>
            <option>All</option>
            <option>Hypermarket</option>
            <option>Supermarket</option>
          </select>
        </div>
      </div>

      {/* ANALYSIS GRID */}
      <div style={grid2}>
        <div style={card}>
          <h4>Category Strength Analysis</h4>
          <div style={chartBox}>
            <Radar data={categoryRadar} options={chartOptions} />
          </div>
        </div>

        <div style={card}>
          <h4>Outlet Contribution</h4>
          <div style={chartBox}>
            <Doughnut data={outletDoughnut} options={chartOptions} />
          </div>
        </div>
      </div>

      <div style={grid2}>
        <div style={card}>
          <h4>Monthly Sales Trend</h4>
          <div style={chartBox}>
            <Line data={stateTrend} options={chartOptions} />
          </div>
        </div>

        <div style={card}>
          <h4>Customer Age Spend Pattern</h4>
          <div style={chartBox}>
            <Bar data={ageBar} options={chartOptions} />
          </div>
        </div>
      </div>

    </div>
  );
};

/* ================= STYLES ================= */

const page = {
  background: "#f8fafc",
  minHeight: "100vh",
  padding: "20px"
};

const subtitle = {
  color: "#64748b",
  marginBottom: "20px"
};

const card = {
  background: "#fff",
  padding: "20px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};

const filters = {
  display: "flex",
  gap: "14px",
  flexWrap: "wrap"
};

const grid2 = {
  display: "grid",
  gridTemplateColumns: "1fr 1fr",
  gap: "20px",
  marginBottom: "20px"
};

export default Analysis;
