import React from "react";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
);

const Dashboard = () => {

  /* ===== KPI DATA ===== */
  const kpiData = [
    { title: "Total Sales", value: "₹42.75 Cr", color: "#16a34a" },
    { title: "Total Products Sold", value: "5.1 L", color: "#2563eb" },
    { title: "Avg Monthly Sales", value: "₹285.00 L", color: "#f97316" },
    { title: "Total Stores Analyzed", value: "75", color: "#9333ea" }
  ];

  /* ===== YEAR WISE SALES ===== */
  const salesTrendData = {
    labels: ["2020", "2021", "2022", "2023", "2024"],
    datasets: [
      {
        label: "Total Sales",
        data: [4, 6, 8, 11, 14],
        borderColor: "#f97316",
        backgroundColor: "#f97316",
        tension: 0.4
      }
    ]
  };

  /* ===== REGIONAL ANALYSIS ===== */
  const regionData = {
    labels: ["North", "South", "East", "West"],
    datasets: [
      {
        data: [11.2, 10.5, 9.8, 9.3],
        backgroundColor: "#f97316"
      }
    ]
  };

  /* ===== TOP CATEGORIES ===== */
  const categories = [
    { name: "Electronics", sales: "₹16.10 Cr", units: "51.9K", percent: 37.7 },
    { name: "Grocery", sales: "₹7.49 Cr", units: "132.2K", percent: 17.5 },
    { name: "Home & Kitchen", sales: "₹7.31 Cr", units: "95.3K", percent: 17.1 },
    { name: "Clothing", sales: "₹6.73 Cr", units: "113.3K", percent: 15.7 }
  ];

  return (
    <div style={{ padding: "20px", background: "#f8fafc" }}>

      {/* ===== HEADER ===== */}
      <h2>Big Bazaar Sales Prediction System</h2>
      <p style={{ color: "#64748b" }}>
        Business Performance Overview & Analysis
      </p>

      {/* ===== FILTERS ===== */}
      <div style={{ display: "flex", gap: "15px", marginBottom: "20px" }}>
        <select><option>All Years</option></select>
        <select><option>All Categories</option></select>
        <select><option>All Locations</option></select>
        <select><option>All Outlet Types</option></select>
      </div>

      {/* ===== KPI CARDS ===== */}
      <div style={{ display: "grid", gridTemplateColumns: "repeat(4,1fr)", gap: "15px" }}>
        {kpiData.map((item, index) => (
          <div
            key={index}
            style={{
              background: "#fff",
              padding: "20px",
              borderRadius: "10px",
              boxShadow: "0 2px 8px rgba(0,0,0,0.05)"
            }}
          >
            <h4 style={{ color: "#64748b" }}>{item.title}</h4>
            <h2 style={{ color: item.color }}>{item.value}</h2>
          </div>
        ))}
      </div>

      {/* ===== CHART SECTION ===== */}
      <div style={{ display: "grid", gridTemplateColumns: "2fr 1fr", gap: "20px", marginTop: "25px" }}>
        <div style={{ background: "#fff", padding: "20px", borderRadius: "10px" }}>
          <h4>Year-wise Sales Trend</h4>
          <Line data={salesTrendData} />
        </div>

        <div style={{ background: "#fff", padding: "20px", borderRadius: "10px" }}>
          <h4>Regional Analysis</h4>
          <Bar data={regionData} options={{ indexAxis: "y" }} />
        </div>
      </div>

      {/* ===== TOP CATEGORIES ===== */}
      <div style={{ background: "#fff", padding: "20px", borderRadius: "10px", marginTop: "25px" }}>
        <h4>Top Selling Categories</h4>

        {categories.map((cat, index) => (
          <div key={index} style={{ marginBottom: "15px" }}>
            <strong>#{index + 1} {cat.name}</strong>
            <div style={{ fontSize: "13px", color: "#64748b" }}>
              Sales: {cat.sales} &nbsp; | &nbsp; Units: {cat.units}
            </div>
            <div style={{ background: "#e5e7eb", height: "8px", borderRadius: "5px", marginTop: "5px" }}>
              <div
                style={{
                  width: `${cat.percent}%`,
                  background: "#ef4444",
                  height: "8px",
                  borderRadius: "5px"
                }}
              ></div>
            </div>
          </div>
        ))}
      </div>

    </div>
  );
};

export default Dashboard;
