import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import {
  Bar,
  Line,
  Pie,
  Doughnut,
  Radar
} from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  PointElement,
  LineElement,
  RadialLinearScale,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  PointElement,
  LineElement,
  RadialLinearScale,
  Tooltip,
  Legend
);

const Analysis = () => {

  /* ================= FILTER STATE ================= */
  const [year, setYear] = useState("All");
  const [category, setCategory] = useState("All");
  const [region, setRegion] = useState("All");
  const [outlet, setOutlet] = useState("All");

  /* ================= DATA STATE ================= */
  const [charts, setCharts] = useState(null);
  const [loading, setLoading] = useState(true);

  /* ================= FETCH ANALYSIS DATA ================= */
  useEffect(() => {
    setLoading(true);

    fetch(
      `http://localhost:5001/api/analysis?year=${year}&category=${category}&region=${region}&outletType=${outlet}`
    )
      .then(res => res.json())
      .then(data => {
        setCharts(data.charts);
        setLoading(false);
      })
      .catch(err => { 
        console.error(err);
        setLoading(false);
      });

  }, [year, category, region, outlet]);

  if (loading || !charts) {
    return <div style={{ padding: 40 }}>Loading analysis...</div>;
  }

  /* ================= CHART CONFIGS ================= */

  const radarData = {
    labels: charts.categorySales.labels,
    datasets: [{
      label: "Category Sales Strength (â‚¹)",
      data: charts.categorySales.data,
      backgroundColor: "rgba(249,115,22,0.3)",
      borderColor: "#f97316",
      borderWidth: 2,
      pointBackgroundColor: "#f97316"
    }]
  };

  const categoryProfitBar = {
    labels: charts.categoryProfit.labels,
    datasets: [{
      label: "Category Profit (â‚¹)",
      data: charts.categoryProfit.data,
      backgroundColor: "#22c55e"
    }]
  };

  const outletPie = {
    labels: charts.outletSales.labels,
    datasets: [{
      data: charts.outletSales.data,
      backgroundColor: ["#f97316", "#fb923c", "#fdba74"]
    }]
  };

  const regionPie = {
    labels: charts.regionSales.labels,
    datasets: [{
      data: charts.regionSales.data,
      backgroundColor: ["#22c55e", "#86efac", "#4ade80"]
    }]
  };

  const trendLine = {
    labels: charts.categorySales.labels,
    datasets: [{
      label: "Sales Trend Snapshot",
      data: charts.categorySales.data,
      borderColor: "#2563eb",
      tension: 0.4
    }]
  };

  /* ================= OPTIONS ================= */
  const normalChart = { maintainAspectRatio: false };
  const radarBox = { height: "420px" };     // ðŸ”¥ BIGGER radar
  const chartBox = { height: "300px" };

  return (
    <div style={page}>
      <Navbar />

      <h2>Deep Sales Analysis</h2>
      <p style={subtitle}>Filters-driven analytical insights</p>

      {/* FILTERS */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={filters}>
          <select onChange={e => setYear(e.target.value)}>
            <option>All</option>
            <option>2022</option>
            <option>2023</option>
            <option>2024</option>
          </select>

          <select onChange={e => setCategory(e.target.value)}>
            <option>All</option>
            <option>Electronics</option>
            <option>Grocery</option>
            <option>Clothing</option>
            <option>Home & Kitchen</option>
          </select>

          <select onChange={e => setRegion(e.target.value)}>
            <option>All</option>
            <option>North</option>
            <option>South</option>
            <option>East</option>
            <option>West</option>
          </select>

          <select onChange={e => setOutlet(e.target.value)}>
            <option>All</option>
            <option>Hypermarket</option>
            <option>Supermarket</option>
            <option>Convenience Store</option>
          </select>
        </div>
      </div>

      {/* RADAR */}
      <div style={card}>
        <h4>Category Strength Analysis</h4>
        <div style={radarBox}>
          <Radar data={radarData} options={normalChart} />
        </div>
      </div>

      {/* GRID 1 */}
      <div style={grid2}>
        <div style={card}>
          <h4>Category Profit Comparison</h4>
          <div style={chartBox}>
            <Bar data={categoryProfitBar} options={normalChart} />
          </div>
        </div>

        <div style={card}>
          <h4>Sales Trend Snapshot</h4>
          <div style={chartBox}>
            <Line data={trendLine} options={normalChart} />
          </div>
        </div>
      </div>

      {/* GRID 2 â€“ PIE CHARTS */}
      <div style={grid2}>
        <div style={card}>
          <h4>Outlet Type Contribution</h4>
          <div style={chartBox}>
            <Pie data={outletPie} options={normalChart} />
          </div>
        </div>

        <div style={card}>
          <h4>Regional Sales Distribution</h4>
          <div style={chartBox}>
            <Doughnut data={regionPie} options={normalChart} />
          </div>
        </div>
      </div>

    </div>
  );
};

/* ================= STYLES ================= */

const page = {
  background: "#f8fafc",
  minHeight: "100vh",
  padding: "20px"
};

const subtitle = {
  color: "#64748b",
  marginBottom: "20px"
};

const card = {
  background: "#fff",
  padding: "20px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)",
  marginBottom: "20px"
};

const filters = {
  display: "flex",
  gap: "14px",
  flexWrap: "wrap"
};

const grid2 = {
  display: "grid",
  gridTemplateColumns: "1fr 1fr",
  gap: "20px"
};

export default Analysis;
