import React from "react";
import { Line, Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  BarElement,
  Tooltip,
  Legend
);

const Dashboard = () => {

  const kpiData = [
    { title: "Total Sales", value: "‚Çπ42.75 Cr", icon: "üí∞" },
    { title: "Total Products Sold", value: "5.1 L", icon: "üì¶" },
    { title: "Avg Monthly Sales", value: "‚Çπ285.00 L", icon: "üìà" },
    { title: "Total Stores Analyzed", value: "75", icon: "üè¨" }
  ];

  const salesTrendData = {
    labels: ["2020", "2021", "2022", "2023", "2024"],
    datasets: [
      {
        label: "Total Sales",
        data: [4, 6, 8, 11, 14],
        borderColor: "#f97316",
        backgroundColor: "#f97316",
        tension: 0.4
      }
    ]
  };

  const regionData = {
    labels: ["North", "South", "East", "West"],
    datasets: [
      {
        data: [11.2, 10.5, 9.8, 9.3],
        backgroundColor: "#f97316"
      }
    ]
  };

  const categories = [
    { name: "Electronics", sales: "‚Çπ16.10 Cr", units: "51.9K", percent: 37.7 },
    { name: "Grocery", sales: "‚Çπ7.49 Cr", units: "132.2K", percent: 17.5 },
    { name: "Home & Kitchen", sales: "‚Çπ7.31 Cr", units: "95.3K", percent: 17.1 },
    { name: "Clothing", sales: "‚Çπ6.73 Cr", units: "113.3K", percent: 15.7 }
  ];

  return (
    <div style={{ background: "#f8fafc", minHeight: "100vh", padding: "20px" }}>

      {/* ================= NAVBAR ================= */}
      <div
        style={{
          background: "#fff",
          padding: "14px 22px",
          borderRadius: "14px",
          display: "flex",
          justifyContent: "space-between",
          alignItems: "center",
          boxShadow: "0 4px 12px rgba(0,0,0,0.05)",
          marginBottom: "24px"
        }}
      >
        <div style={{ display: "flex", alignItems: "center", gap: "12px" }}>
          <div
            style={{
              width: "38px",
              height: "38px",
              background: "#f97316",
              borderRadius: "10px",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              color: "#fff",
              fontSize: "18px"
            }}
          >
            üõí
          </div>
          <div>
            <h3 style={{ margin: 0 }}>Big Bazaar Sales Prediction System</h3>
            <p style={{ margin: 0, fontSize: "13px", color: "#64748b" }}>
              Business Performance Overview & Analysis
            </p>
          </div>
        </div>

       <div style={{ display: "flex", gap: "10px" }}>
  <NavLink to="/" style={({ isActive }) => navBtn(isActive)}>
    Dashboard
  </NavLink>

  <NavLink to="/analysis" style={({ isActive }) => navBtn(isActive)}>
    Analysis
  </NavLink>

  <NavLink to="/predict" style={({ isActive }) => navBtn(isActive)}>
    Sales Prediction
  </NavLink>
</div>

      </div>

      {/* ================= FILTERS ================= */}
      <div style={cardStyle}>
        <h4 style={{ marginBottom: "12px" }}>Filters</h4>
        <div style={{ display: "flex", gap: "14px", flexWrap: "wrap" }}>
          {["All Years", "All Categories", "All Locations", "All Outlet Types"].map((f, i) => (
            <select key={i} style={filterStyle}>
              <option>{f}</option>
            </select>
          ))}
        </div>
      </div>

      {/* ================= KPI CARDS ================= */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "repeat(4,1fr)",
          gap: "16px",
          marginTop: "22px"
        }}
      >
        {kpiData.map((k, i) => (
          <div key={i} style={kpiCard}>
            <div style={{ fontSize: "22px" }}>{k.icon}</div>
            <p style={{ color: "#64748b", margin: "6px 0" }}>{k.title}</p>
            <h2 style={{ margin: 0 }}>{k.value}</h2>
          </div>
        ))}
      </div>

      {/* ================= CHARTS ================= */}
      <div
        style={{
          display: "grid",
          gridTemplateColumns: "2fr 1fr",
          gap: "20px",
          marginTop: "24px"
        }}
      >
        <div style={cardStyle}>
          <h4>Year-wise Sales Trend</h4>
          <Line data={salesTrendData} />
        </div>

        <div style={cardStyle}>
          <h4>Regional Analysis</h4>
          <Bar data={regionData} options={{ indexAxis: "y" }} />
        </div>
      </div>

      {/* ================= TOP CATEGORIES ================= */}
      <div style={{ ...cardStyle, marginTop: "24px" }}>
        <h4>Top Selling Categories</h4>

        {categories.map((c, i) => (
          <div key={i} style={{ marginTop: "14px" }}>
            <strong>#{i + 1} {c.name}</strong>
            <div style={{ fontSize: "13px", color: "#64748b" }}>
              Sales: {c.sales} | Units: {c.units}
            </div>
            <div style={progressBg}>
              <div style={{ ...progressFill, width: `${c.percent}%` }} />
            </div>
          </div>
        ))}
      </div>

    </div>
  );
};

/* ================= STYLES ================= */

const cardStyle = {
  background: "#fff",
  padding: "20px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};

const filterStyle = {
  padding: "10px 14px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb",
  minWidth: "180px",
  cursor: "pointer"
};

const navBtn = (active) => ({
  padding: "8px 16px",
  borderRadius: "999px",
  border: "none",
  cursor: "pointer",
  background: active ? "#f97316" : "#f1f5f9",
  color: active ? "#fff" : "#334155",
  transition: "all 0.25s ease",
    textDecoration: "none",

});

const kpiCard = {
  background: "#fff",
  padding: "18px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)",
  transition: "transform 0.25s ease",
};

const progressBg = {
  background: "#e5e7eb",
  height: "8px",
  borderRadius: "6px",
  marginTop: "6px"
};

const progressFill = {
  height: "8px",
  background: "#ef4444",
  borderRadius: "6px"
};

export default Dashboard;
