import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend
);

const Analysis = () => {
  /* ================= FILTER STATE ================= */
  const [filters, setFilters] = useState({
    year: "",
    category: "",
    region: "",
    outletType: ""
  });

  const [data, setData] = useState(null);

  useEffect(() => {
    fetchAnalysis();
  }, [filters]);

  const fetchAnalysis = async () => {
    const res = await axios.get("http://localhost:5001/api/analysis", {
      params: filters
    });
    setData(res.data);
  };

  if (!data) return <p>Loading analysis...</p>;

  /* ================= PIE OPTIONS ================= */
  const pieOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        position: "top"
      }
    }
  };

  /* ================= PRODUCT CATEGORY ================= */
  const categoryChart = {
    labels: data.category.labels,
    datasets: [
      {
        label: "Sales (‚Çπ Cr)",
        data: data.category.sales,
        backgroundColor: "#f97316"
      },
      {
        label: "Profit (‚Çπ Cr)",
        data: data.category.profit,
        backgroundColor: "#22c55e"
      }
    ]
  };

  /* ================= DEMOGRAPHICS ================= */
  const ageChart = {
    labels: data.age.labels,
    datasets: [
      {
        data: data.age.sales,
        backgroundColor: ["#f97316", "#fb923c", "#fdba74", "#fed7aa"]
      }
    ]
  };

  const cityTierChart = {
    labels: data.cityTier.labels,
    datasets: [
      {
        data: data.cityTier.sales,
        backgroundColor: ["#f97316", "#fb923c", "#fdba74"]
      }
    ]
  };

  /* ================= GEOGRAPHIC ================= */
  const topStateIndex = data.states.sales.indexOf(
    Math.max(...data.states.sales)
  );

  const topRegionIndex = data.regions.growth.indexOf(
    Math.max(...data.regions.growth)
  );

  return (
    <div style={{ padding: "20px", background: "#f8fafc", minHeight: "100vh" }}>
      <Navbar />

      {/* ================= FILTERS ================= */}
<div style={card}>
  <h4>Analysis Filters</h4>
  <div style={filterRow}>
    <select
      style={filter}
      onChange={e => setFilters({ ...filters, year: e.target.value })}
    >
      <option value="">All Years</option>
      <option>2020</option>
      <option>2021</option>
      <option>2022</option>
      <option>2023</option>
      <option>2024</option>
    </select>

    <select
      style={filter}
      onChange={e => setFilters({ ...filters, category: e.target.value })}
    >
      <option value="">All Categories</option>
      {data.category.labels.map(c => (
        <option key={c}>{c}</option>
      ))}
    </select>

    <select
      style={filter}
      onChange={e => setFilters({ ...filters, region: e.target.value })}
    >
      <option value="">All Regions</option>
      {data.regions.labels.map(r => (
        <option key={r}>{r}</option>
      ))}
    </select>
  </div>
</div>

{/* ================= CATEGORY ANALYSIS ================= */}
<section style={section}>
  <h3>Product Category Analysis</h3>

  <div style={grid2}>
    {/* Category Chart */}
    <div style={card}>
      {/* üîß FIXED HEIGHT TO AVOID BIG GRAPH */}
      <div style={{ height: "320px" }}>
        <Bar
          data={categoryChart}
          options={{
            responsive: true,
            maintainAspectRatio: false,
            plugins: {
              legend: {
                position: "top"
              }
            }
          }}
        />
      </div>
    </div>

    {/* High / Low Performer */}
    <div style={card}>
      <h4>High Performer</h4>
      <p style={{ color: "#16a34a", fontWeight: "600" }}>
        {data.highLow.high.name} ‚Äì ‚Çπ{data.highLow.high.sales} Cr
