};

const filterRow = {
  display: "flex",
  gap: "14px",
  flexWrap: "wrap"
};

const filter = {
  padding: "10px 14px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb",
  minWidth: "180px"
};

export default Analysis;
