import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import { Bar } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(CategoryScale, LinearScale, BarElement, Tooltip, Legend);

const Analysis = () => {
  const [data, setData] = useState(null);
  const [filters, setFilters] = useState({
    year: "All",
    category: "All",
    region: "All",
    outletType: "All"
  });

  useEffect(() => {
    axios
      .get("http://localhost:5001/api/analysis", { params: filters })
      .then((res) => setData(res.data))
      .catch((err) => console.error(err));
  }, [filters]);

  if (!data) return <p>Loading analysis...</p>;

  /* ===== CATEGORY BAR CHART ===== */
  const categoryLabels = Object.keys(data.categoryMap);
  const salesData = categoryLabels.map(
    (c) => data.categoryMap[c].sales / 1e7
  );
  const profitData = categoryLabels.map(
    (c) => data.categoryMap[c].profit / 1e7
  );

  const categoryChart = {
    labels: categoryLabels,
    datasets: [
      {
        label: "Sales (Cr)",
        data: salesData,
        backgroundColor: "#f97316"
      },
      {
        label: "Profit (Cr)",
        data: profitData,
        backgroundColor: "#22c55e"
      }
    ]
  };

  const highPerformer = categoryLabels.reduce((a, b) =>
    data.categoryMap[a].sales > data.categoryMap[b].sales ? a : b
  );
  const lowPerformer = categoryLabels.reduce((a, b) =>
    data.categoryMap[a].sales < data.categoryMap[b].sales ? a : b
  );

  return (
    <div style={page}>
      <Navbar />



      {/* ===== HEADER ===== */}
      <h2 style={{ marginBottom: 4 }}>Deep Sales Analysis</h2>
      <p style={subtitle}>
        Understand patterns, trends, and insights behind the numbers
      </p>

      {/* ===== FILTERS ===== */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={filterRow}>
          {["Year", "Category", "Region", "Outlet Type"].map((f) => (
            <select
              key={f}
              style={filter}
              onChange={(e) =>
                setFilters({ ...filters, [f.toLowerCase()]: e.target.value })
              }
            >
              <option>All {f}s</option>
            </select>
          ))}
        </div>
      </div>

      {/* ===== PRODUCT CATEGORY ANALYSIS ===== */}
      <div style={{ ...card, marginTop: 20 }}>
        <h4>Product Category Analysis</h4>

        <div style={{ display: "grid", gridTemplateColumns: "3fr 1fr", gap: 20 }}>
          <div>
            <h5>Category-wise Sales & Profit</h5>
            <Bar data={categoryChart} height={120} />
          </div>

          <div>
            <div style={goodBox}>
              <strong>High Performer</strong>
              <p>{highPerformer}</p>
              <span>₹{(data.categoryMap[highPerformer].sales / 1e7).toFixed(2)} Cr</span>
            </div>

            <div style={badBox}>
              <strong>Low Performer</strong>
              <p>{lowPerformer}</p>
              <span>₹{(data.categoryMap[lowPerformer].sales / 1e7).toFixed(2)} Cr</span>
            </div>
          </div>
        </div>
      </div>

      {/* ===== TABLE ===== */}
      <div style={{ ...card, marginTop: 20 }}>
        <h4>Detailed Product Metrics</h4>
        <table style={table}>
          <thead>
            <tr>
              <th>Category</th>
              <th>Total Sales</th>
              <th>Units Sold</th>
              <th>Avg Price</th>
              <th>Profit Margin</th>
              <th>Status</th>
            </tr>
          </thead>
          <tbody>
            {data.categoryMetrics.map((c, i) => (
              <tr key={i}>
                <td>{c.category}</td>
                <td>₹{(c.totalSales / 1e7).toFixed(2)} Cr</td>
                <td>{c.unitsSold}</td>
                <td>₹{Math.round(c.avgPrice)}</td>
                <td>{c.profitMargin}%</td>
                <td>
                  <span style={badge}>{c.status}</span>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </div>
  );
};



/* ===== STYLES ===== */
const page = { background: "#f8fafc", minHeight: "100vh", padding: 20 };
const subtitle = { color: "#64748b", marginBottom: 20 };
const card = {
  background: "#fff",
  padding: 18,
  borderRadius: 14,
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};
const filterRow = { display: "flex", gap: 14, flexWrap: "wrap" };
const filter = {
  padding: "10px 14px",
  borderRadius: 10,
  border: "1px solid #e5e7eb",
  minWidth: 160
};
const goodBox = {
  background: "#ecfdf5",
  padding: 12,
  borderRadius: 12,
  marginBottom: 12
};
const badBox = {
  background: "#fef2f2",
  padding: 12,
  borderRadius: 12
};
const table = {
  width: "100%",
  borderCollapse: "collapse",
  marginTop: 10
};
const badge = {
  background: "#fee2e2",
  padding: "4px 10px",
  borderRadius: 999,
  fontSize: 12
};

export default Analysis;
