import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import axios from "axios";
import { Bar, Pie } from "react-chartjs-2";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  ArcElement,
  Tooltip,
  Legend
);

const Analysis = () => {
  const [filters, setFilters] = useState({
    year: "",
    category: "",
    region: "",
    outletType: ""
  });

  const [data, setData] = useState(null);

  useEffect(() => {
    fetchAnalysis();
  }, [filters]);

  const fetchAnalysis = async () => {
    const res = await axios.get("http://localhost:5001/api/analysis", {
      params: filters
    });
    setData(res.data);
  };

  /* ✅ FINAL SAFETY CHECK */
  if (!data) return <p>Loading analysis...</p>;

  /* ================= CHART DATA ================= */

  const categoryChart = {
    labels: data.category.labels,
    datasets: [
      {
        label: "Sales (₹ Cr)",
        data: data.category.sales,
        backgroundColor: "#f97316"
      },
      {
        label: "Profit (₹ Cr)",
        data: data.category.profit,
        backgroundColor: "#22c55e"
      }
    ]
  };

  const ageChart = {
    labels: data.age.labels,
    datasets: [
      {
        data: data.age.sales,
        backgroundColor: ["#f97316", "#fb923c", "#fdba74", "#fed7aa"]
      }
    ]
  };

  const cityTierChart = {
    labels: data.cityTier.labels,
    datasets: [
      {
        data: data.cityTier.sales,
        backgroundColor: ["#f97316", "#fb923c", "#fdba74"]
      }
    ]
  };

  const stateChart = {
    labels: data.states.labels,
    datasets: [
      {
        label: "Sales (₹ Cr)",
        data: data.states.sales,
        backgroundColor: "#f97316"
      },
      {
        label: "Avg Store Sales (₹ L)",
        data: data.states.avg,
        backgroundColor: "#22c55e"
      }
    ]
  };

  const regionChart = {
    labels: data.regions.labels,
    datasets: [
      {
        label: "Sales (₹ Cr)",
        data: data.regions.sales,
        backgroundColor: "#f97316"
      }
    ]
  };

  return (
    <div style={{ padding: "20px", background: "#f8fafc", minHeight: "100vh" }}>
      <Navbar />

      {/* ================= FILTERS ================= */}
      <div style={card}>
        <h4>Analysis Filters</h4>
        <div style={filterRow}>
          <select style={filter} onChange={e => setFilters({ ...filters, year: e.target.value })}>
            <option value="">All Years</option>
            <option>2020</option>
            <option>2021</option>
            <option>2022</option>
            <option>2023</option>
            <option>2024</option>
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, category: e.target.value })}>
            <option value="">All Categories</option>
            {data.category.labels.map(c => (
              <option key={c}>{c}</option>
            ))}
          </select>

          <select style={filter} onChange={e => setFilters({ ...filters, region: e.target.value })}>
            <option value="">All Regions</option>
            {data.regions.labels.map(r => (
              <option key={r}>{r}</option>
            ))}
          </select>
        </div>
      </div>

      {/* ================= CATEGORY ================= */}
      <section style={section}>
        <h3>Product Category Analysis</h3>
        <div style={grid2}>
          <div style={card}><Bar data={categoryChart} /></div>
          <div style={card}>
            <h4>High Performer</h4>
            <p style={{ color: "#16a34a", fontWeight: "600" }}>
              {data.highLow.high.name} – ₹{data.highLow.high.sales} Cr
            </p>
            <h4>Low Performer</h4>
            <p style={{ color: "#dc2626", fontWeight: "600" }}>
              {data.highLow.low.name} – ₹{data.highLow.low.sales} Cr
            </p>
          </div>
        </div>
      </section>

      {/* ================= DEMOGRAPHICS ================= */}
      <section style={section}>
        <h3>Customer Demographics</h3>
        <div style={grid2}>
          <div style={card}><Pie data={ageChart} /></div>
          <div style={card}><Pie data={cityTierChart} /></div>
        </div>
      </section>

      {/* ================= GEOGRAPHY ================= */}
      <section style={section}>
        <h3>Geographic Analysis</h3>
        <div style={grid2}>
          <div style={card}><Bar data={stateChart} /></div>
          <div style={card}><Bar data={regionChart} /></div>
        </div>
      </section>
    </div>
  );
};

/* ================= STYLES ================= */

const card = {
  background: "#fff",
  padding: "18px",
  borderRadius: "14px",
  boxShadow: "0 6px 18px rgba(0,0,0,0.05)"
};

const section = { marginTop: "28px" };

const grid2 = {
  display: "grid",
  gridTemplateColumns: "2fr 1fr",
  gap: "18px"
};

const filterRow = {
  display: "flex",
  gap: "14px",
  flexWrap: "wrap"
};

const filter = {
  padding: "10px 14px",
  borderRadius: "10px",
  border: "1px solid #e5e7eb",
  minWidth: "180px"
};

export default Analysis;
